mapper.select:
 select
*
 from t_uploadfile
 where 1=1

     AND Uf_domainName=#{domainName}


     AND Uf_domainId=#{domainId}

mapper.insert:
insert into t_uploadfile (Uf_id, Uf_domainName, Uf_domainId,
  Uf_fileName, Uf_fileDownloadUri, Uf_fileType,
  Uf_size, Uf_customInfo,Uf_savePath,Uf_originalPath, Uf_recordTime
  )
values (#{urId,jdbcType=BIGINT}, #{urDomainname,jdbcType=VARCHAR}, #{urDomainid,jdbcType=BIGINT}, 
  #{urFilename,jdbcType=VARCHAR}, #{urFiledownloaduri,jdbcType=VARCHAR}, #{urFiletype,jdbcType=VARCHAR}, 
  #{urSize,jdbcType=BIGINT}, #{urCustominfo,jdbcType=VARCHAR}, #{urSavePath,jdbcType=VARCHAR},#{urOriginalPath,jdbcType=VARCHAR},
  #{urRecordtime,jdbcType=DATE}
  )
